/**
 * –ü–∞–¥–∞—é—â–∏–π —à–∞—Ä —Å –∑–∞—Ö–≤–∞—Ç–æ–º –ª—É—á–æ–º
 */

import { World, PhysicsSystem, PhysicsBody, PhysicsShape, PhysicsState, PhysicsShapeType, Interactable, DistanceGrabbable } from '@iwsdk/core';
import * as THREE from 'three';

const world = (window as any).__IWSDK_WORLD__ as World;

// === –†–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–µ–º —Ñ–∏–∑–∏–∫—É ===
try {
  world
    .registerSystem(PhysicsSystem, { configData: { gravity: [0, -9.81, 0] } })
    .registerComponent(PhysicsBody)
    .registerComponent(PhysicsShape);
  console.log('‚úÖ Physics system registered!');
} catch (e) {
  console.log('‚ö†Ô∏è Physics registration error:', e);
}

// === Cleanup tracking ===
const entities: any[] = [];
const geometries: THREE.BufferGeometry[] = [];
const materials: THREE.Material[] = [];

// === –ù–µ–≤–∏–¥–∏–º—ã–π –ø–æ–ª –¥–ª—è —Ñ–∏–∑–∏–∫–∏ ===
const floorGeo = new THREE.BoxGeometry(10, 0.1, 10);
const floorMat = new THREE.MeshBasicMaterial({ visible: false });
const floor = new THREE.Mesh(floorGeo, floorMat);
floor.position.set(0, 0, 0);

const floorEntity = world.createTransformEntity(floor);
floorEntity.addComponent(PhysicsShape, {
  shape: PhysicsShapeType.Box,
  dimensions: [10, 0.1, 10]
});
floorEntity.addComponent(PhysicsBody, { state: PhysicsState.Static });

entities.push(floorEntity);
geometries.push(floorGeo);
materials.push(floorMat);

// === –ü–∞–¥–∞—é—â–∏–π —à–∞—Ä (Dynamic) ===
const sphereGeo = new THREE.SphereGeometry(0.15, 32, 32);
const sphereMat = new THREE.MeshStandardMaterial({
  color: 0xff4444,
  emissive: 0x331111,
  roughness: 0.3,
  metalness: 0.7
});
const sphere = new THREE.Mesh(sphereGeo, sphereMat);
sphere.position.set(0, 2, -1);

const sphereEntity = world.createTransformEntity(sphere);

// –§–∏–∑–∏–∫–∞
sphereEntity.addComponent(PhysicsShape, {
  shape: PhysicsShapeType.Sphere,
  dimensions: [0.15],
  restitution: 0.7
});
sphereEntity.addComponent(PhysicsBody, { state: PhysicsState.Dynamic });

// –ó–∞—Ö–≤–∞—Ç –ª—É—á–æ–º
sphereEntity.addComponent(Interactable);
sphereEntity.addComponent(DistanceGrabbable, {
  maxDistance: 10,
  translate: true,
  rotate: true,
  scale: false
});

entities.push(sphereEntity);
geometries.push(sphereGeo);
materials.push(sphereMat);

console.log('üî¥ –®–∞—Ä: –ø–∞–¥–∞–µ—Ç + —Ö–≤–∞—Ç–∞–π –ª—É—á–æ–º!');

// === HMR Cleanup ===
if (import.meta.hot) {
  import.meta.hot.accept();
  import.meta.hot.dispose(() => {
    entities.forEach(e => { try { e.destroy(); } catch {} });
    geometries.forEach(g => g.dispose());
    materials.forEach(m => m.dispose());
  });
}

